// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../node_modules/@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract TokenBalanceFetcher {

    function getBalances(address owner, address[] memory tokenAddresses) external view returns (uint256[] memory) {
        uint8 i = 0;
        uint256[] memory balances = new uint256[](tokenAddresses.length);


        while(i < tokenAddresses.length) {
            ERC20 tokenContract = ERC20(tokenAddresses[i]);
            balances[i] = tokenContract.balanceOf(owner);
        }
        
        
        return balances;
    }

}
